    def get_input_file_name(self):
        filename = QtWidgets.QFileDialog.getOpenFileName(self.input_browse_button,
                                                     "Select PDF File",
                                                     "",
                                                     "PDF Files (*.pdf)")
        self.input_lineEdit.setText(filename[0])
        print(str(filename))

    def get_output_dir(self):
        output_dir = QtWidgets.QFileDialog.getExistingDirectory(self.output_browse_button_2,
                                                                "Select Output Directory",
                                                                "")
        self.output_line_edit.setText(output_dir)
        print(str(output_dir))

    def on_convert_clicked(self):
        #self.resetProgBars()
        self.startProgressBar_read()
        input_file = self.input_lineEdit.text()
        output_dest = self.output_line_edit.text()
        file_type = self.filetype_comboBox.currentText()
        print(file_type)
        desired_pages = self.desired_pages_lineEdit.text()
        print(desired_pages)
        compress_files = self.compress_checkBox.isChecked()
        print(compress_files)
        track_to_txt = self.track_checkbox.isChecked()
        print(track_to_txt)
        self.disable_convert_button()
        # controller.start_converter(input_file, output_dest, file_type, desired_pages, compress_files, track_to_txt, self)
        self.startConversion(input_file,output_dest,file_type,desired_pages,compress_files,track_to_txt)


    def startConversion(self, input_file,output_dest,file_type,desired_pages,compress_files,track_to_txt):
        self.convertThread = converterThread(input_file=input_file,
                                             output_dest=output_dest,
                                             file_type=file_type,
                                             desired_pages=desired_pages,
                                             compress_files=compress_files,
                                             track_to_txt=track_to_txt)
        self.convertThread.finished.connect(self.enable_convert_button)
        self.convertThread.start()


    def startProgressBar_read(self):
        self.thread = ReadThread()
        self.thread.change_val.connect(self.setProgressVal_read)
        self.thread.change_val.connect(self.getTotalProg)
        self.thread.finished_read.connect(self.startProgressBar_convert)
        self.thread.start()

    def setProgressVal_read(self, val):
        self.read_bar.setValue(round(val * 100))

    def startProgressBar_convert(self):
        self.thread_con = ConvertThread()
        self.thread_con.change_val_convert.connect(self.setProgressVal_conv)
        self.thread_con.change_val_convert.connect(self.getTotalProg)
        self.thread_con.finished_convert.connect(self.startProgressBar_error)
        self.thread_con.start()

    def setProgressVal_conv(self, val):
        self.convert_bar.setValue(round(val * 100))

    def startProgressBar_error(self):
        self.thread_err = ErrorCheckThread()
        self.thread_err.change_val_error.connect(self.setProgressVal_err)
        self.thread_err.change_val_error.connect(self.getTotalProg)
        self.thread_err.finished.connect(self.startProgressBar_concatandarchive)
        self.thread_err.start()

    def setProgressVal_err(self, val):
        self.error_bar.setValue(round(val * 100))

    def startProgressBar_concatandarchive(self):
        self.thread_caa = ConcatAndArchiveThread()
        self.thread_caa.change_val_concatandarchive.connect(self.setProgressVal_caa)
        self.thread_caa.change_val_concatandarchive.connect(self.getTotalProg)
        self.thread_caa.start()

    def setProgressVal_caa(self, val):
        # print(f"setting concat bar to {val}")
        self.concat_bar.setValue(round(val * 100))

    def startProgressBar_total(self):
        self.thread_tot = TotalThread()
        self.thread_tot.change_val_total.connect(self.setProgressVal_tot)
        self.thread_tot.start()

    def setProgressVal_tot(self, val):
        self.total_bar.setValue(round(val * 100))


    def disable_convert_button(self):
        self.conver_button.setDisabled(True)
        self.conver_button.setText("Converting..")

    def enable_convert_button(self):
        self.conver_button.setEnabled(True)
        self.conver_button.setText("Convert")

    def getTotalProg(self):
        totProg = (self.read_bar.value() + self.convert_bar.value() + self.error_bar.value() + self.concat_bar.value()) / 4
        self.total_bar.setValue(round(totProg))

    def resetProgBars(self):
        self.read_bar.setValue(0)
        self.convert_bar.setValue(0)
        self.error_bar.setValue(0)
        self.concat_bar.setValue(0)
        self.total_bar.setValue(0)

